# Helper function to shift a note by a specific number of semitones
def shift_note(note, semitone_shift):
    chromatic_scale = ["C", "C#", "D", "D#", "E", "F", "F#", "G", "G#", "A", "A#", "B"]
    base_note = note[:-1]  # Get the note (e.g., C, D#)
    octave = int(note[-1])  # Get the octave number
    new_index = (chromatic_scale.index(base_note) + semitone_shift) % 12
    new_octave = octave + (chromatic_scale.index(base_note) + semitone_shift) // 12
    return chromatic_scale[new_index] + str(new_octave)

# Generate chords for a given root note
def generate_chords(root, intervals):
    chords = []
    for octave in range(1, 9):
        root_note = root + str(octave)
        chord = [root_note] + [shift_note(root_note, i) for i in intervals]
        # Generate inversions
        inversions = [
            chord,
            [chord[2], chord[0], chord[1]],
            [chord[1], chord[2], shift_note(chord[0], 12)],
        ]
        chords.extend(inversions)
    return chords

# Chromatic scale
chromatic_scale = ["C", "C#", "D", "D#", "E", "F", "F#", "G", "G#", "A", "A#", "B"]

# Intervals for major and minor chords
major_intervals = [4, 7]
minor_intervals = [3, 7]

# Create expanded dictionaries
MAJOR_CHORDS = {}
MINOR_CHORDS = {}

for note in chromatic_scale:
    MAJOR_CHORDS[f"{note}_Major"] = generate_chords(note, major_intervals)
    MINOR_CHORDS[f"{note}_Minor"] = generate_chords(note, minor_intervals)

# Print results
print("MAJOR_CHORDS =", MAJOR_CHORDS)
print("MINOR_CHORDS =", MINOR_CHORDS)
